import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

export default function AddTask({ onSave, editingTask, cancelEdit }) {
  const [title, setTitle]       = useState('');
  const [status, setStatus]     = useState('Pending');
  const [deadline, setDeadline] = useState(new Date());

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title);
      setStatus(editingTask.status);
      setDeadline(new Date(editingTask.deadline));
    }
  }, [editingTask]);

  const handleSubmit = e => {
    e.preventDefault();
    onSave({ title, status, deadline });
    setTitle(''); setStatus('Pending'); setDeadline(new Date());
  };

  return (
    <form onSubmit={handleSubmit} className="card p-3">
      <h5>{editingTask ? 'Edit Task' : 'Add Task'}</h5>
      <div className="mb-2">
        <label>Task</label>
        <input className="form-control" value={title} onChange={e => setTitle(e.target.value)} required/>
      </div>
      <div className="mb-2">
        <label>Status</label>
        <select className="form-control" value={status} onChange={e => setStatus(e.target.value)}>
          <option>Pending</option><option>Ongoing</option><option>Completed</option>
        </select>
      </div>
      <div className="mb-2">
        <label>Deadline</label>
        <DatePicker
          selected={deadline}
          onChange={date => setDeadline(date)}
          showTimeSelect
          dateFormat="Pp"
          className="form-control"
        />
      </div>
      <button className="btn btn-success" type="submit">
        {editingTask ? 'Update' : 'Add'} Task
      </button>{' '}
      {editingTask && (
        <button type="button" className="btn btn-secondary" onClick={cancelEdit}>
          Cancel
        </button>
      )}
    </form>
  );
}
